import Link from 'next/link'

const Card = (props) => {
   return (
     <div id={props.post.id} className="card order-3 p-2 bd-highlight m-2 mxw">
       <img
         id={props.post.id}
         src={props?.post?.url}
         className="card-img-top"
         alt={props?.post?.imgeTitle}
       ></img>
       <div id={props.post.id} className="card-body">
         <h5 id={props.post.id} className="card-title">
           {props.post.title}
         </h5>
         <p id={props.post.id} className="card-text">
           {props.post.body}
         </p>
         <p id={props.post.id} className="text-muted">
           {props?.post.userName}
         </p>
         <Link id={props.post.id} href="#">
           <a className="btn btn-primary">Find more...</a>
         </Link>
       </div>
     </div>
   );

}

export default Card
