import Link from 'next/link'

const Card = (props) => {
   return (
     <div className="card order-3 p-2 bd-highlight m-2 mxw">
       <img
         src={props?.post?.url}
         class="card-img-top"
         alt={props?.post?.i}
       ></img>
       <div className="card-body">
         <h5 className="card-title">{props.post.title}</h5>
         <p className="card-text">{props.post.body}</p>
         <p className="text-muted">{props?.post.userName}</p>
         <Link href="#">
           <a className="btn btn-primary">Find more...</a>
         </Link>
       </div>
     </div>
   );

}

export default Card
