   useEffect(() => {
    const getApi = async () => {
       try {
         console.log('api')
        const responce = await fetch(
          "https://jsonplaceholder.typicode.com/posts"
        );
        const data = await responce.json();
         setData(data);
         console.log( data[0] )

      } catch (err) {
        console.log({err});
      }
   };
      getApi();

   }, []);

  return (
    <Layout>
      <div>
           <div>Welcome to Ignites</div>
            {data?.map(item => item.id)}
      </div>
    </Layout>
  );
};

