const main = (state = {
    name: "guest",
    loading: false,
    error: null,
    todos: []
}, action) => {
  switch(action.type){
    case "SET_NAME":
      return { 
        ...state,
        name: action.payload
      };
    case "REGISTER":
      return { 
        ...state,
        name: action.payload.name,
        email: action.payload.email,
        age: action.payload.age,
        token: action.payload.token,
        loading: false,
        error: null
      };
    case "LOADING":
      return { 
        ...state,
        loading: action.payload
      };
    case "SIGN_OUT":
      return {
        name: "guest",
        loading: false
      }
    case "ERROR":
      return {
        ...state,
        error: action.payload
      }
    case "GET_TODOS":
      return {
        ...state,
        todos: action.payload
      }
    case "CREATE_TODO":
      return {
        ...state,
        todos: state.todos.concat(action.payload)
      }
    case "DELETE_TODO":
      return {
        ...state,
        todos: action.payload
      }
    case "UPDATE_TODOS":
      return {
        ...state,
        todos: action.payload
      }
    default:
      return {...state};
  }
};

export default main;
