const initialState = {
  id:'',
  name: '',
  age: 0,
}
const testReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TEST":
      state = state + 1
      return state;
    default: return state
  }
}
export default testReducer;
