import React from "react";
import { wrapper } from "../redux/store";
//import "../style/App.css";

const MyApp_Modify = ({ Component, pageProps }) => {
  return (
    <div className="app">
      <Component {...pageProps} />

      <style jsx>{`
        body {
          font-family: "Raleway", /* 'Montserrat', */ "Quicksand",
            "Montserrat Subrayada", sans-serif;
        }

        p {
          font-size: 0.9em;
        }

        .badge {
          font-weight: 600;
        }
        .fa-react {
          font-size: 1em;
          text-shadow: 0 1px 0 #fff;
          margin-right: 7px;
        }

        .mxw {
          width: 18rem;
        }
      `}</style>
    </div>
  );
};

export default wrapper.withRedux(MyApp_Modify);
