const initialState = {
  id:'',
  name: '',
  age: 0,
}
const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_USER":
      state = {
        id: action.pay
      }
      return state;
    default: return state
  }
}
export default testReducer;
